function doGet(e) {
  let page = e && e.parameter.mode ? e.parameter.mode : "Index";
  let html = HtmlService.createTemplateFromFile(page).evaluate();
  let htmlContent = html.getContent();
  let metaTag = '<meta name="viewport" content="width=device-width, initial-scale=1.0">';

  htmlContent = htmlContent.replace('<head>', `<head>${metaTag}`);
  htmlContent = htmlContent.replace("{{NAVBAR}}", getNavbar(page));
  let htmlOutput = HtmlService.createHtmlOutput(htmlContent);
  htmlOutput.setTitle("Inventory Management System");
  return htmlOutput;
}



function getNavbar(activePage) {
  const scriptURLHome = getScriptURL();
  const scriptURLInventory = getScriptURL("mode=Inventory");
  const scriptURLTransaction = getScriptURL("mode=Transaction");
  const scriptURLShipment = getScriptURL("mode=Shipment");
  const scriptURLSettings = getScriptURL("mode=settings");
  const scriptURLLineage = getScriptURL("mode=Lineage");
  const scriptURLPackaging = getScriptURL("mode=Packaging");
  const scriptURLOrder = getScriptURL("mode=Order");
  const scriptURLReport = getScriptURL("mode=Report");
  const scriptURLTempRcd = getScriptURL("mode=TempRcd");
  const scriptURLSL = getScriptURL("mode=SL");
  const scriptURLMaintenance = getScriptURL("mode=Maintenance");


  return `
    <nav>
      <div class="nav-container">
        <ul>
          <li><a href="${scriptURLHome}" class="${activePage === 'Index' ? 'active' : ''}">Dashboard</a></li>
          <li><a href="${scriptURLInventory}" class="${activePage === 'Inventory' ? 'active' : ''}">Inventory</a></li>
          <li><a href="${scriptURLShipment}" class="${activePage === 'Shipment' ? 'active' : ''}">Shipments</a></li>
          <li><a href="${scriptURLOrder}" class="${activePage === 'Order' ? 'active' : ''}">Order</a></li>
          <li><a href="${scriptURLLineage}" class="${activePage === 'Lineage' ? 'active' : ''}">Lineage</a></li>
          <li><a href="${scriptURLSL}" class="${activePage === 'SL' ? 'active' : ''}">Sing Long</a></li>
          <li><a href="${scriptURLPackaging}" class="${activePage === 'Packaging' ? 'active' : ''}">Packaging Material</a></li>
          <li><a href="${scriptURLTempRcd}" class="${activePage === 'TempRcd' ? 'active' : ''}">Coldroom Temperature</a></li>
          <li><a href="${scriptURLMaintenance}" class="${activePage === 'Maintenance' ? 'active' : ''}">Service Record</a></li>
          <li><a href="${scriptURLReport}" class="${activePage === 'Report' ? 'active' : ''}">Report</a></li>
        </ul>
      </div>
    </nav>`;
}

function getScriptURL(qs = null) {
  let url = ScriptApp.getService().getUrl();
  if (qs) {
    url += qs.indexOf("?") === -1 ? "?" + qs : qs;
  }
  return url;
}


//INCLUDE HTML PARTS, EG. JAVASCRIPT, CSS, OTHER HTML FILES
function include(filename) {
  return HtmlService.createHtmlOutputFromFile(filename).getContent();

}

function getSheetNames() {
  const spreadsheetId = '1JjXIxCMKWN3Z9xhLHa4HHoxK4cx48Ex5iUz4vx4TjMs';
  const sheets = SpreadsheetApp.openById(spreadsheetId).getSheets();
  const sheetNames = sheets.map(sheet => sheet.getName());

  // Get the last 30 sheet names
  return sheetNames.slice(-30);
}

function getInventory(sheetName) {
  const spreadsheetId = '1JjXIxCMKWN3Z9xhLHa4HHoxK4cx48Ex5iUz4vx4TjMs';
  const sheet = SpreadsheetApp.openById(spreadsheetId).getSheetByName(sheetName);

  if (!sheet) {
    throw new Error('Sheet not found: ' + sheetName);
  }

  const data = sheet.getRange(3, 1, sheet.getLastRow() - 2, sheet.getLastColumn()).getValues();
  return data.map(row => ({
    id: row[1],
    name: row[2],
    category: row[3],
    packing: row[5],
    quantity: row[6]
  }));
}

function getTransactions(itemId) {
  const sheet = SpreadsheetApp.getActiveSpreadsheet().getSheetByName('Transactions');
  const data = sheet.getDataRange().getValues();
  const headers = data[0];
  const transactions = data.slice(1).filter(row => row[headers.indexOf('ItemID')] == itemId);

  return transactions.map(row => ({
    date: row[headers.indexOf('Date')],
    quantity: row[headers.indexOf('Quantity')],
    type: row[headers.indexOf('Type')]
  }));
}




function addItem(item) {
  const sheet = SpreadsheetApp.getActiveSpreadsheet().getSheetByName('Inventory');
  sheet.appendRow([item.id, item.name, item.category, item.quantity, item.price]);
}

function deleteItem(id) {
  const sheet = SpreadsheetApp.getActiveSpreadsheet().getSheetByName('Inventory');
  const data = sheet.getDataRange().getValues();
  for (let i = 1; i < data.length; i++) {
    if (data[i][0] == id) {
      sheet.deleteRow(i + 1);
      break;
    }
  }
}


function getTransactions(itemId) {
  // Replace the spreadsheet ID below with your actual spreadsheet ID
  var spreadsheetId = '15jj7ZQgL1fIPsHr-kl3s2Quy7TrLsjQ-sjV4-lcus8o';
  var sheetName1 = 'InventoryTranscationRecord';  // Existing sheet name
  var sheetName2 = 'CR5 transfer to PR';      // New sheet name

  // Get data from the first sheet (InventoryTranscationRecord)
  var sheet1 = SpreadsheetApp.openById(spreadsheetId).getSheetByName(sheetName1);
  var data1 = sheet1.getDataRange().getValues();

  // Get data from the second sheet (Blk15 unbox Carton STM)
  var sheet2 = SpreadsheetApp.openById(spreadsheetId).getSheetByName(sheetName2);
  var data2 = sheet2.getDataRange().getValues();

  // Prepare arrays to hold transactions
  var transactions1 = [];
  var transactions2 = [];

  // Helper function to format date
  function formatDate(rawDate) {
    // Assuming rawDate is in Singapore time
    var singaporeTimeZone = 'GMT+08:00';  // Singapore time zone offset

    // Convert rawDate to Date object and adjust for Singapore time zone
    var date = new Date(rawDate + ' ' + singaporeTimeZone);

    // Format the date
    var formattedDate = Utilities.formatDate(date, singaporeTimeZone, 'dd/MM/yyyy');

    return formattedDate;
  }

  // Process transactions from the first sheet (InventoryTranscationRecord)
  for (var i = 1; i < data1.length; i++) { // Start from 1 to skip header row
    var row = data1[i];

    // Check if the item ID matches in the first sheet
    if (row[4] == itemId) { // Assuming item ID is in the fifth column (E)
      // Format the date
      var formattedDate = formatDate(row[7]); // Assuming date is in the eighth column (H)

      var transaction = {
        date: formattedDate,
        from: row[2],       // Assuming 'from' is in the third column (C)
        to: row[3],         // Assuming 'to' is in the fourth column (D)
        quantity: row[1]    // Assuming 'quantity' is in the second column (B)
      };

      transactions1.push(transaction);
    }
  }

  // Process transactions from the second sheet (Blk15 unbox Carton STM)
  for (var j = 1; j < data2.length; j++) { // Start from 1 to skip header row
    var row2 = data2[j];

    // Check if the item ID matches in the second sheet
    if (row2[0] == itemId) { // Assuming item ID is in the first column (A)
      // Format the date
      var formattedDate2 = formatDate(row2[3]); // Assuming date is in the seventh column (G)

      var transaction2 = {
        date: formattedDate2,
        quantity: row2[5]   // Assuming 'quantity' is in the ninth column (I)
      };

      transactions2.push(transaction2);
    }
  }

  // Return both sets of transactions
  return { transactions1: transactions1, transactions2: transactions2 };
}


// Load SheetJS library code directly into Apps Script
eval(UrlFetchApp.fetch('https://cdnjs.cloudflare.com/ajax/libs/xlsx/0.17.4/xlsx.full.min.js').getContentText());

function processExcel(fileData) {
  try {
    var bytes = Utilities.base64Decode(fileData.split(',')[1]); // Decode base64 data
    var workbook = XLSX.read(bytes, { type: 'array' }); // Parse Excel file

    // Assume the first sheet is the target
    var firstSheetName = workbook.SheetNames[0];
    var worksheet = workbook.Sheets[firstSheetName];

    // Convert worksheet to JSON format starting from row 6
    var jsonData = XLSX.utils.sheet_to_json(worksheet, { header: 1, range: 5 }); // Start from row 6 (0-based index)

    // Extract rows 1 to 5 separately (if needed for header/introductory information)

    var rows1to5 = [];
    for (var r = 0; r < 5; r++) {
      var row = [];
      for (var c in jsonData[r]) {
        row.push({ column: c, value: jsonData[r][c] });
      }
      rows1to5.push(row);
    }

    return { headers: jsonData[0], data: jsonData.slice(1), rows1to5: rows1to5 }; // Return headers, data, and rows 1-5 to client-side JavaScript
  } catch (e) {
    Logger.log(e.stack || e.toString()); // Log error
    throw new Error('Error processing the Excel file: ' + e.message); // Throw error message
  }
}


function updateGoogleSheet(date, itemCode, productDescription, packing, type, expiryBatchNo, grn, lineageItemCode, dateStored, containerNo, quantity, pallet) {
  try {
    var ss = SpreadsheetApp.openById('1GQUKpmn6dp0CJRz873w8x0LZf8EyA6xPQ3HPFlh99qs');
    var drawOutSheet = ss.getSheetByName('Draw Out');

    if (!drawOutSheet) {
      throw new Error("Sheet 'Draw Out' not found in the spreadsheet");
    }

    // Prepare data to append, with date at the last column
    var rowData = [itemCode, productDescription, packing, type, expiryBatchNo, grn, lineageItemCode, dateStored, containerNo, quantity, pallet, date];

    // Update Draw Out sheet
    drawOutSheet.appendRow(rowData);

    return "Successfully updated the 'Draw Out' sheet";
  } catch (error) {
    Logger.log("Error in updateGoogleSheet: " + error.message);
    return "Error: " + error.message;
  }
}

function updateLineageInventory(sheet, itemCode, lineageItemCode, quantity, pallet) {
  var data = sheet.getDataRange().getValues();

  for (var i = 1; i < data.length; i++) {
    if (data[i][0] == itemCode && data[i][6] == lineageItemCode) {
      var balQuantity = sheet.getRange(i+1, 10).getValue();
      var balPallet = sheet.getRange(i+1, 11).getValue();

      sheet.getRange(i+1, 10).setValue(balQuantity - parseFloat(quantity));
      sheet.getRange(i+1, 11).setValue(balPallet - parseFloat(pallet));

      break;
    }
  }
}


function storeData(data, storedDate, containerNumber, grn) {
  var ss = SpreadsheetApp.openById('1GQUKpmn6dp0CJRz873w8x0LZf8EyA6xPQ3HPFlh99qs');
  var sheet = ss.getSheetByName('Stock In');

  // Check if sheet exists
  if (!sheet) {
    console.error('Sheet "Stock In" not found');
    return;
  }

  // Append data to the sheet
  data.forEach(function(row) {
    var newRow = [
      row[0],           // ITEM CODE
      row[1],           // PRODUCT DESCRIPTION
      row[2],           // PACKING
      row[3],           // TYPE
      row[4],           // Expiry Date / Batch No
      grn,              // GRN (from function parameter)
      row[5],           // Lineage Item Code
      storedDate,       // DATE STORED (from function parameter)
      containerNumber,  // CONTAINER (from function parameter)
      row[6],           // Quantity
      row[7]            // Pallets
    ];

    sheet.appendRow(newRow);
    Logger.log('Row appended: ' + newRow); // Log the appended row for verification
  });

  Logger.log('Data stored successfully!');
}

function IncomingFunction() {
  try {
    var sheet = SpreadsheetApp.openById('1485ut13dOQHzyosLWs7afhWVCve6asyeGSB0RI6vv4E').getSheetByName('Shipment List');
    var data = sheet.getDataRange().getValues();
    var headers = data[0];
    var rows = data.slice(1);

    var today = new Date();
    var sevenDaysLater = new Date(today.getTime() + 7 * 24 * 60 * 60 * 1000);

    function parseDate(dateString) {
      var parts = dateString.split('/');
      return new Date(parts[2], parts[1] - 1, parts[0]);
    }

    var filteredShipments = rows.filter(function(row) {
      var unloadDate = parseDate(row[5]); // Assuming Unload Date is in column F (index 5)
      return unloadDate >= today && unloadDate <= sevenDaysLater;
    });

    var formattedShipments = filteredShipments.map(function(row) {
      return [
        cleanShipmentNumber(row[0]), // Shipment No (cleaned)
        row[1], // PO No
        row[2], // Container Number
        formatDate(row[3]), // ETA (formatted to DD/MM/YYYY)
        row[4], // Trucking By
        row[5]  // Unload Date (already in correct format)
      ];
    });

    // Add headers to the formatted shipments
    formattedShipments.unshift(headers);

    return formattedShipments;
  } catch (error) {
    return [headers]; // Return at least the headers if there's an error
  }
}

function formatDate(date) {
  if (typeof date === 'string') {
    // If the date is already a string, assume it's in the correct format
    return date;
  }
  if (!(date instanceof Date) || isNaN(date)) {
    return '';
  }
  var day = date.getDate().toString().padStart(2, '0');
  var month = (date.getMonth() + 1).toString().padStart(2, '0');
  var year = date.getFullYear();
  return `${day}/${month}/${year}`;
}

function cleanShipmentNumber(shipmentNo) {
  // Remove "SHIPMENT: " prefix if it exists
  return shipmentNo.replace(/^SHIPMENT:\s*/i, '');
}

//Incoming Order
function getAllPO() {
  const SHEET_ID = '12K9NImc1eQm-hvBbep5_9i-vQdRRo5vdR9SdqysuQ80';
  const SHEET_NAMES = ['SCORE PO', 'Foodfare Catering PO', 'NTUC Foodfare PO', 'CMM PO'];

  let allData = {};

  try {
    const spreadsheet = SpreadsheetApp.openById(SHEET_ID);

    const spreadsheetTimeZone = spreadsheet.getSpreadsheetTimeZone();
    const today = new Date();
    const tomorrow = new Date(today.getTime() + 24 * 60 * 60 * 1000);
    const threeDaysLater = new Date(today.getTime() + 4 * 24 * 60 * 60 * 1000); // 3 days after tomorrow

    const tomorrowFormatted = Utilities.formatDate(tomorrow, spreadsheetTimeZone, 'yyyy-MM-dd');
    const threeDaysLaterFormatted = Utilities.formatDate(threeDaysLater, spreadsheetTimeZone, 'yyyy-MM-dd');

    SHEET_NAMES.forEach(sheetName => {
      const sheet = spreadsheet.getSheetByName(sheetName);
      if (!sheet) {
        return; // Skip to next sheet
      }

      const data = sheet.getDataRange().getValues();

      let sheetData = {};

      data.forEach((row, index) => {
        if (index === 0) return; // Skip header row

        const dateString = Utilities.formatDate(new Date(row[0]), spreadsheetTimeZone, 'yyyy-MM-dd');
        if (dateString >= tomorrowFormatted && dateString <= threeDaysLaterFormatted) {
          if (!sheetData[dateString]) {
            sheetData[dateString] = { uniqueValues: new Set() };
          }
          sheetData[dateString].uniqueValues.add(row[1]); // Add value from column B to the Set
        }
      });

      // Convert Sets to counts
      Object.keys(sheetData).forEach(date => {
        sheetData[date] = { count: sheetData[date].uniqueValues.size };
      });

      allData[sheetName] = sheetData;
    });

    return allData;

  } catch (error) {
    return { error: error.message };
  }
}

function getDetailedPOData(date, poType) {
  const SHEET_ID = '12K9NImc1eQm-hvBbep5_9i-vQdRRo5vdR9SdqysuQ80';
  let sheetNames, descriptionColumn, quantityColumn, unit;

  switch(poType) {
    case 'SCORE PO':
      sheetNames = ['SCORE PO'];
      descriptionColumn = 'C';
      quantityColumn = 'F';
      unit = 'Pkts';
      break;
    case 'NTUC PO':
      sheetNames = ['NTUC Foodfare PO', 'Foodfare Catering PO'];
      descriptionColumn = 'C';
      quantityColumn = 'E';
      unit = 'Ctns';
      break;
    case 'CMM PO':
      sheetNames = ['CMM PO'];
      descriptionColumn = 'C';
      quantityColumn = 'E';
      unit = 'Ctns';
      break;
    default:
      throw new Error('Invalid PO Type');
  }

  let detailedData = {};

  sheetNames.forEach(sheetName => {
    const sheet = SpreadsheetApp.openById(SHEET_ID).getSheetByName(sheetName);
    const data = sheet.getDataRange().getValues();

    data.forEach((row, index) => {
      if (index === 0) return; // Skip header row
      const rowDate = new Date(row[0]);
      if (Utilities.formatDate(rowDate, sheet.getParent().getSpreadsheetTimeZone(), 'yyyy-MM-dd') === date) {
        const description = row[descriptionColumn.charCodeAt(0) - 65]; // Convert column letter to index
        const quantity = parseFloat(row[quantityColumn.charCodeAt(0) - 65]) || 0;

        if (detailedData[description]) {
          detailedData[description] += quantity;
        } else {
          detailedData[description] = quantity;
        }
      }
    });
  });

  // Convert the object to an array of objects, round the quantity, and add the unit
  return Object.entries(detailedData).map(([description, quantity]) => ({
    description,
    quantity: `${Math.round(quantity)} ${unit}`
  }));
}

function createInventorySummary() {
  var ss = SpreadsheetApp.openById('1GQUKpmn6dp0CJRz873w8x0LZf8EyA6xPQ3HPFlh99qs');
  var openingStockSheet = ss.getSheetByName('Jun 2024');
  var stockInSheet = ss.getSheetByName('Stock In');
  var drawOutSheet = ss.getSheetByName('Draw Out');
  var summarySheet = ss.getSheetByName('Inventory Summary');
  var timezone = ss.getSpreadsheetTimeZone();

  // If the Inventory Summary sheet doesn't exist, create it
  if (!summarySheet) {
    summarySheet = ss.insertSheet('Inventory Summary');
  } else {
    summarySheet.clear(); // Clear existing data
  }

  function formatDates(data) {
    return data.map(row => 
      row.map(cell => 
        cell instanceof Date 
          ? Utilities.formatDate(cell, timezone, "dd/MM/yyyy") 
          : cell
      )
    );
  }

  var openingStock = formatDates(openingStockSheet.getRange('A2:K').getValues());
  var stockIn = formatDates(stockInSheet.getRange('A2:K').getValues());
  var drawOut = formatDates(drawOutSheet.getRange('A2:K').getValues());

  var summaryData = [];
  summaryData.push(['ITEM CODE', 'PRODUCT DESCRIPTION', 'PACKING', 'TYPE', 'Expiry Date / Batch No', 'GRN', 'Lineage Item Code', 'DATE STORED', 'CONTAINER', 'Opening BAL. CTNS', 'Opening BAL. Plt', 'Stock In BAL. CTNS', 'Stock In Pallet', 'Draw Out BAL. CTNS', 'Draw Out Pallet', 'Summary Ctns', 'Summary Plt']);

  var stockMap = {};

  function generateKey(entry) {
    return entry.map(item => String(item).trim()).join('_');
  }

  // Process opening stock
  for (var i = 0; i < openingStock.length; i++) {
    var itemCode = openingStock[i][0];
    if (itemCode) {
      var key = generateKey(openingStock[i].slice(0, 9));
      if (!stockMap[key]) {
        stockMap[key] = {
          details: openingStock[i].slice(0, 9),
          openingCtns: 0,
          openingPlts: 0,
          stockInCtns: 0,
          stockInPlts: 0,
          drawOutCtns: 0,
          drawOutPlts: 0,
          summaryCtns: 0,
          summaryPlts: 0
        };
      }
      stockMap[key].openingCtns += openingStock[i][9] || 0;
      stockMap[key].openingPlts += openingStock[i][10] || 0;
      stockMap[key].summaryCtns += openingStock[i][9] || 0;
      stockMap[key].summaryPlts += openingStock[i][10] || 0;
    }
  }

  // Process stock in
  for (var j = 0; j < stockIn.length; j++) {
    var itemCodeIn = stockIn[j][0];
    if (itemCodeIn) {
      var keyIn = generateKey(stockIn[j].slice(0, 9));
      if (!stockMap[keyIn]) {
        stockMap[keyIn] = {
          details: stockIn[j].slice(0, 9),
          openingCtns: 0,
          openingPlts: 0,
          stockInCtns: 0,
          stockInPlts: 0,
          drawOutCtns: 0,
          drawOutPlts: 0,
          summaryCtns: 0,
          summaryPlts: 0
        };
      }
      stockMap[keyIn].stockInCtns += stockIn[j][9] || 0;
      stockMap[keyIn].stockInPlts += stockIn[j][10] || 0;
      stockMap[keyIn].summaryCtns += stockIn[j][9] || 0;
      stockMap[keyIn].summaryPlts += stockIn[j][10] || 0;
    }
  }

  // Process draw out
  for (var k = 0; k < drawOut.length; k++) {
    var itemCodeOut = drawOut[k][0];
    if (itemCodeOut) {
      var keyOut = generateKey(drawOut[k].slice(0, 9));
      if (stockMap[keyOut]) {
        stockMap[keyOut].drawOutCtns += drawOut[k][9] || 0;
        stockMap[keyOut].drawOutPlts += drawOut[k][10] || 0;
        stockMap[keyOut].summaryCtns -= drawOut[k][9] || 0;
        stockMap[keyOut].summaryPlts -= drawOut[k][10] || 0;
      }
    }
  }

  // Prepare summary data
  for (var item in stockMap) {
    summaryData.push([
      ...stockMap[item].details, 
      stockMap[item].openingCtns, 
      stockMap[item].openingPlts, 
      stockMap[item].stockInCtns, 
      stockMap[item].stockInPlts, 
      stockMap[item].drawOutCtns, 
      stockMap[item].drawOutPlts, 
      stockMap[item].summaryCtns, 
      stockMap[item].summaryPlts
    ]);
  }

  // Set summary data to the sheet
  summarySheet.getRange(1, 1, summaryData.length, summaryData[0].length).setValues(summaryData);
}


function getInventoryData() {
  const sheet = SpreadsheetApp.openById('1GQUKpmn6dp0CJRz873w8x0LZf8EyA6xPQ3HPFlh99qs').getSheetByName('Inventory Summary');
  const dataRange = sheet.getDataRange();
  const numRows = dataRange.getNumRows();
  const numCols = dataRange.getNumColumns();
  const values = dataRange.getValues();

  // Array to hold the filtered data
  let filteredData = [];

  // Loop through each row and extract columns 0-8 and 15-16
  for (let i = 0; i < numRows; i++) {
    let row = [];
    let shouldIncludeRow = true; // Flag to determine if row should be included

    for (let j = 0; j < numCols; j++) {
      if (j >= 0 && j <= 8) {
        // Include columns 0-8
        row.push(values[i][j]);
      } else if (j === 15 || j === 16) {
        // Include columns 15 and 16
        row.push(values[i][j]);
        // Check if column 15 is 0; if so, set shouldIncludeRow to false
        if (j === 15 && values[i][j] === 0) {
          shouldIncludeRow = false;
        }
      }
    }

    // Format dates in columns 4 (index 3) and 7 (index 6) to "DD/MM/YYYY"
    if (row[3] instanceof Date) {
      row[3] = Utilities.formatDate(row[3], Session.getScriptTimeZone(), "dd/MM/yyyy");
    }
    if (row[6] instanceof Date) {
      row[6] = Utilities.formatDate(row[6], Session.getScriptTimeZone(), "dd/MM/yyyy");
    }

    // Add row to filteredData if shouldIncludeRow is true
    if (shouldIncludeRow) {
      filteredData.push(row);
    }
  }

  // Convert all dates to formatted strings before stringifying
  filteredData = filteredData.map(row => 
    row.map(cell => 
      cell instanceof Date ? Utilities.formatDate(cell, Session.getScriptTimeZone(), "dd/MM/yyyy") : cell
    )
  );

  return JSON.stringify(filteredData);
}


// Report
function generateEndMonthReport() {
  var spreadsheet = SpreadsheetApp.openById('1GQUKpmn6dp0CJRz873w8x0LZf8EyA6xPQ3HPFlh99qs');
  var sheet = spreadsheet.getSheetByName('Inventory Summary');
  var data = sheet.getDataRange().getValues();

  var filteredData = [];
  for (var i = 1; i < data.length; i++) {
    if (data[i][15] !== 0) { // Index 15 (column P) check
      filteredData.push([
        data[i][0], 
        data[i][1], 
        data[i][2], 
        data[i][3], 
        formatDate(data[i][4]), // Format date in column 4 (index 4)
        data[i][5], 
        data[i][6], 
        formatDate(data[i][7]), // Format date in column 7 (index 7)
        data[i][8], 
        data[i][15], 
        data[i][16]
      ]);
    }
  }

  // Generate HTML content for PDF
  var monthName = getMonthName(new Date().getMonth() + 1);
  var htmlContent = generateHtmlContent(filteredData, monthName);

  // Create PDF
  var pdf = createPdf(htmlContent);

  // Save the PDF to Drive and generate download link
  var pdfFile = DriveApp.createFile(pdf).setName('EndMonthReport.pdf');
  var pdfUrl = pdfFile.getDownloadUrl();
