function generateHtmlContent(data, monthName) {
  var rowsPerPage = 15; // Adjust this number based on your needs
  var totalPages = Math.ceil(data.length / rowsPerPage);

  var html = '<html><head><style>';
  html += '@page { size: A4; margin: 0; }';
  html += 'body { margin: 0; padding: 20mm 20mm 40mm 20mm; }';
  html += 'table { width: 100%; border-collapse: collapse; margin-top: 120px; }'; // Added margin-top
  html += 'th, td { border: 1px solid black; padding: 5px; text-align: left; }';
  html += 'header { position: fixed; top: 0; left: 0; right: 0; height: 100px; text-align: center; background: white; }';
  html += 'footer { position: fixed; bottom: 0; left: 0; right: 0; height: 30px; text-align: center; background: white; }';
  html += '.page-break { page-break-before: always; }';
  html += '</style></head><body>';

  for (var page = 1; page <= totalPages; page++) {
    if (page > 1) {
      html += '<div class="page-break"></div>';
    }

    html += '<header><h1>Li Chuan Food Product Pte Ltd</h1><h2>' + monthName + ' Report</h2></header>';
    html += '<table><thead><tr>';
    html += '<th>Item Code</th><th>Product Description</th><th>Packing</th><th>Type</th><th>Expiry Date / Batch No</th><th>GRN</th><th>Lineage Item Code</th><th>Date Stored</th><th>Container</th><th>BAL. CTNS</th><th>BAL. PLTS</th>';
    html += '</tr></thead><tbody>';

    var startRow = (page - 1) * rowsPerPage;
    var endRow = Math.min(page * rowsPerPage, data.length);

    for (var i = startRow; i < endRow; i++) {
      html += '<tr>';
      for (var j = 0; j < data[i].length; j++) {
        html += '<td>' + data[i][j] + '</td>';
      }
      html += '</tr>';
    }

    html += '</tbody></table>';
    html += '<footer>Page ' + page + ' of ' + totalPages + '</footer>';
  }

  html += '</body></html>';
  return html;
}
