//Catering and foodfare
function getCateringData(selectedDate) {
  try {
    const ss = SpreadsheetApp.openById('12K9NImc1eQm-hvBbep5_9i-vQdRRo5vdR9SdqysuQ80');

    const foodfareCateringSheet = ss.getSheetByName('Foodfare Catering PO');
    const ntucFoodfareSheet = ss.getSheetByName('NTUC Foodfare PO');
    const foodfareCateringDateSheet = ss.getSheetByName('Foodfare Catering Date');

    if (!foodfareCateringSheet || !ntucFoodfareSheet || !foodfareCateringDateSheet) {
      throw new Error('One or more required sheets not found');
    }

    const foodfareCateringData = getDataFromSheet(foodfareCateringSheet, selectedDate, 'Foodfare Catering PO');
    const ntucFoodfareData = getDataFromSheet(ntucFoodfareSheet, selectedDate, 'NTUC Foodfare PO');
    const previouslySubmittedData = getPreviouslySubmittedData(foodfareCateringDateSheet, selectedDate);

    let mergedFoodfareCateringData = foodfareCateringData.map(item => {
      const previousData = previouslySubmittedData.find(prev => prev.productName === item.productName);
      return {
        ...item,
        batchNo: previousData ? previousData.batchNo : '',
        expDate: previousData ? previousData.expDate : ''
      };
    });

    previouslySubmittedData.forEach(prevItem => {
      if (!mergedFoodfareCateringData.some(item => item.productName === prevItem.productName)) {
        mergedFoodfareCateringData.push({
          source: 'Foodfare Catering PO',
          productName: prevItem.productName,
          quantity: 'Not in current PO',
          batchNo: prevItem.batchNo,
          expDate: prevItem.expDate
        });
      }
    });

    const combinedData = mergedFoodfareCateringData.concat(ntucFoodfareData);

    return combinedData.length > 0 ? combinedData : [];
  } catch (error) {
    return [];
  }
}

function formatDateString(dateString) {
  if (dateString instanceof Date) {
    return Utilities.formatDate(dateString, Session.getScriptTimeZone(), 'dd/MM/yyyy');
  } else if (typeof dateString === 'string') {
    if (/^\d{2}\/\d{2}\/\d{4}$/.test(dateString)) {
      return dateString;
    }
    const date = new Date(dateString);
    if (!isNaN(date.getTime())) {
      return Utilities.formatDate(date, Session.getScriptTimeZone(), 'dd/MM/yyyy');
    }
  }
  return dateString;
}

function getPreviouslySubmittedData(sheet, selectedDate) {
  const data = sheet.getDataRange().getValues();
  const headers = data[0];

  const dateColumnIndex = headers.indexOf('Date');
  const productNameColumnIndex = headers.indexOf('Product Name');
  const batchNoColumnIndex = headers.indexOf('Batch No');
  const expDateColumnIndex = headers.indexOf('Exp Date');

  if (dateColumnIndex === -1 || productNameColumnIndex === -1 || batchNoColumnIndex === -1 || expDateColumnIndex === -1) {
    return [];
  }

  const [day, month, year] = selectedDate.split('/');
  const formattedSelectedDate = `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}`;

  const filteredData = data.slice(1)
    .filter(row => {
      const rowDate = row[dateColumnIndex];
      if (rowDate instanceof Date && !isNaN(rowDate)) {
        const formattedRowDate = Utilities.formatDate(rowDate, Session.getScriptTimeZone(), 'yyyy-MM-dd');
        return formattedRowDate === formattedSelectedDate;
      }
      return false;
    });

  return filteredData.map(row => ({
    productName: row[productNameColumnIndex],
    batchNo: row[batchNoColumnIndex].toString(),
    expDate: formatDateString(row[expDateColumnIndex])
  }));
}

function getDataFromSheet(sheet, selectedDate, source) {
  const data = sheet.getDataRange().getValues();
  const headers = data[0];

  const dateColumnIndex = headers.indexOf('Delivery Date');
  const productNameColumnIndex = headers.indexOf('Product Description');
  const quantityColumnIndex = headers.indexOf('Quantity');

  if (dateColumnIndex === -1 || productNameColumnIndex === -1 || quantityColumnIndex === -1) {
    return [];
  }

  const [day, month, year] = selectedDate.split('/');
  const formattedSelectedDate = `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}`;

  const filteredData = data.slice(1)
    .filter(row => {
      const rowDate = row[dateColumnIndex];
      if (rowDate instanceof Date && !isNaN(rowDate)) {
        const formattedRowDate = Utilities.formatDate(rowDate, Session.getScriptTimeZone(), 'yyyy-MM-dd');
        return formattedRowDate === formattedSelectedDate;
      }
      return false;
    });

  return filteredData.map(row => ({
    source: source,
    productName: row[productNameColumnIndex],
    quantity: row[quantityColumnIndex].toString()
  }));
}

