function drawOutPackagingStock(date, itemCode, description, packing, category, quantity, uom) {
  var inventorySheet = SpreadsheetApp.openById(SPREADSHEET_ID).getSheetByName(INVENTORY_SHEET_NAME);
  var drawOutSheet = SpreadsheetApp.openById(SPREADSHEET_ID).getSheetByName(DRAW_OUT_SHEET_NAME);

  var data = inventorySheet.getDataRange().getValues();
  var matchedRow = null;

  for (var i = 1; i < data.length; i++) {
    if (data[i][1] === itemCode && data[i][2].trim() === description.trim()) {
      matchedRow = i;
      break;
    }
    else if (itemCode === "" && data[i][2].trim() === description.trim()) {
      matchedRow = i;
      break;
    }
  }

  if (matchedRow !== null) {
    var currentQuantity = Number(data[matchedRow][4]);
    var newQuantity = currentQuantity - Number(quantity);

    if (newQuantity < 0) {
      return "Error: Not enough stock available. Current: " + currentQuantity + ", Requested: " + quantity;
    }

    inventorySheet.getRange(matchedRow + 1, 5).setValue(newQuantity);

    drawOutSheet.appendRow([date, data[matchedRow][1], data[matchedRow][2], data[matchedRow][3], data[matchedRow][0], quantity, data[matchedRow][5]]);

    return "Stock drawn out successfully";
  }

  return "Error: Item not found in inventory. ItemCode: '" + itemCode + "', Description: '" + description + "'";
}

