function storeInPackagingStock(date, itemCode, description, packing, category, quantity, uom, isNewItem) {
  var inventorySheet = SpreadsheetApp.openById(SPREADSHEET_ID).getSheetByName(INVENTORY_SHEET_NAME);
  var stockInSheet = SpreadsheetApp.openById(SPREADSHEET_ID).getSheetByName(STOCK_IN_SHEET_NAME);

  if (isNewItem) {
    // Add new item to inventory
    inventorySheet.appendRow([category, itemCode, description, packing, quantity, uom]);
  } else {
    // Update existing item
    var data = inventorySheet.getDataRange().getValues();
    for (var i = 1; i < data.length; i++) {
      if (data[i][1] === itemCode) {  // Assuming item code is in column B (index 1)
        var currentQuantity = Number(data[i][4]);  // Assuming quantity is in column E (index 4)
        var newQuantity = currentQuantity + Number(quantity);
        inventorySheet.getRange(i + 1, 5).setValue(newQuantity);  // Update quantity in column E
        break;
      }
    }
  }

  // Record stock in
  stockInSheet.appendRow([date, itemCode, description, packing, category, quantity, uom]);

  return isNewItem ? "New item added and stocked in successfully" : "Stock in successful";
}

