function submitAdjustment(adjustmentData) {
  try {
    console.log("Received adjustment data:", JSON.stringify(adjustmentData));

    var SPREADSHEET_ID = '1DM3hnK4Z5WZWgRYwunP9_P0RV079d2T-QZsd0Z9_FOI';
    var INVENTORY_SHEET_NAME = 'Packaging Materials Summary';
    var ADJUSTMENT_SHEET_NAME = 'Adjustment';

    var inventorySheet = SpreadsheetApp.openById(SPREADSHEET_ID).getSheetByName(INVENTORY_SHEET_NAME);
    var adjustmentSheet = SpreadsheetApp.openById(SPREADSHEET_ID).getSheetByName(ADJUSTMENT_SHEET_NAME);

    if (!inventorySheet || !adjustmentSheet) {
      throw new Error("Unable to find required sheets");
    }

    var updatedData = [];

    adjustmentData.forEach(function(item) {
      // Update inventory sheet
      var data = inventorySheet.getDataRange().getValues();
      var rowUpdated = false;

      for (var j = 1; j < data.length; j++) {
        if (data[j][1] === item.itemCode) {  // Assuming item code is in column B (index 1)
          inventorySheet.getRange(j + 1, 5).setValue(item.actualCount);  // Update quantity in column E
          updatedData.push({
            itemCode: item.itemCode,
            description: data[j][2],  // Assuming description is in column C
            packing: data[j][3],      // Assuming packing is in column D
            category: data[j][0],     // Assuming category is in column E
            quantity: item.actualCount,
            uom: data[j][5]           // Assuming UOM is in column G
          });
          rowUpdated = true;
          break;
        }
      }

      if (!rowUpdated) {
        console.log("Warning: No matching row found for item code:", item.itemCode);
      }

      // Add entry to adjustment sheet
      var date = new Date().toISOString().split('T')[0]; // Get current date in YYYY-MM-DD format
      adjustmentSheet.appendRow([date, item.itemCode, item.description, item.currentQuantity, item.actualCount, item.difference, item.reason]);
    });

    console.log("Updated data to return:", JSON.stringify(updatedData));
    return updatedData;
  } catch (error) {
    console.error("Error in submitAdjustment:", error);
    throw error;
  }
}

function SLgetInventoryData() {
  var ss = SpreadsheetApp.openById('17kO0MzE5mOMcQqdJgHenpSjY4Tll2KMpF8pSIdiXLUo');
  var sheet = ss.getSheetByName('Sing Long Summary');
  var range = sheet.getDataRange();
  var data = range.getValues();
  var backgrounds = range.getBackgrounds();

  console.log('Total rows in sheet: ' + data.length);

  // Remove header row from data, but keep it in backgrounds
  data.shift();

  var today = new Date();

  // Function to parse date strings
  function parseDate(dateString) {
    if (dateString instanceof Date) {
      return dateString;
    }
    var parts = dateString.split('/');
    if (parts.length === 3) {
      return new Date(parts[2], parts[1] - 1, parts[0]); // Year, Month (0-indexed), Day
    }
    return new Date(dateString); // Fallback to default parsing
  }

  // Format date as dd/MM/yyyy
  function formatDate(date) {
    return Utilities.formatDate(date, Session.getScriptTimeZone(), 'dd/MM/yyyy');
  }

  var formattedData = data.map(function(row, rowIndex) {
    // Handle Date Stored (row[5])
    var storedDate = parseDate(row[5]);
    if (isNaN(storedDate.getTime())) {
      storedDate = new Date(); // Use current date if parsing fails
    }
    var storedDateFormatted = formatDate(storedDate);

    // Calculate Pallet Due Date
    var dueDate = new Date(storedDate.getTime());

    // First, add 29 days
    dueDate.setDate(dueDate.getDate() + 29);

    // If still not more than today, keep adding 30 days until it is
    while (dueDate <= today) {
      dueDate.setDate(dueDate.getDate() + 30);
    }

    var dueDateFormatted = formatDate(dueDate);

    var row3Value = row[3] instanceof Date ? formatDate(row[3]) : row[3].toString();

    // Calculate Days Left and format as "X Days"
    var daysLeft = Math.ceil((dueDate - today) / (1000 * 60 * 60 * 24));
    var daysLeftFormatted = daysLeft + " Days";

    var formattedRow = [
      row[0],  // Item Code
      row[1],  // Product Description
      row[2].toString(),  // Packing (as text)
      row3Value,  // Date if it's a date, otherwise string
      row[4],  // Lot No
      storedDateFormatted,  // Date Stored
      row[6],  // Container No
      dueDateFormatted,  // Pallet Due Date (calculated)
      daysLeftFormatted,  // Days Left until Pallet Due Date (e.g., "7 Days")
      row[8],  // Quantity
      row[9]   // Pallets
    ];

    // Add color information, offsetting the rowIndex by 1 to account for the header
    var colors = backgrounds[rowIndex + 1].slice(0, 10);  // Take the first 10 columns

    // Add an extra column for the 'Days Left' and adjust quantity and pallets
    colors.splice(8, 0, '#ffffff');  // Insert white color for 'Days Left' column
    colors.push(colors[9]);  // Duplicate the color for quantity
    colors.push(colors[10]);  // Duplicate the color for pallets

    console.log('Row ' + (rowIndex + 2) + ' colors: ' + JSON.stringify(colors));

    return {
      data: formattedRow,
      colors: colors
    };
  });

  console.log('Total rows processed: ' + formattedData.length);
  console.log('Sample data: ' + JSON.stringify(formattedData[0]));

  return formattedData;
}
