  var formattedDate = dateParts[2] + '/' + dateParts[1] + '/' + dateParts[0];

  // Prepare the row to be added
  var newRow = [
    formattedDate,  // Date in DD/MM/YYYY format
    data.itemCode,
    data.productDescription,
    data.packing,
    data.expiryBatch,
    data.lotNo,
    data.dateStored,
    data.containerNo,
    data.quantity,
    data.pallet
  ];

  // Append the new row to the "Stock Out" sheet
  stockOutSheet.appendRow(newRow);

  // Update the "Sing Long Summary" sheet
  var summarySheet = ss.getSheetByName('Sing Long Summary');
  var summaryData = summarySheet.getDataRange().getValues();

  Logger.log("Number of rows in summary sheet: " + summaryData.length);

  var updated = false;
  for (var i = 1; i < summaryData.length; i++) {  // Start from 1 to skip header
    Logger.log("Checking row " + (i+1) + ": " + JSON.stringify(summaryData[i]));

    if (summaryData[i][0] == data.itemCode && summaryData[i][4] == data.lotNo) {
      // Update quantity and pallets
      var newQuantity = summaryData[i][8] - Number(data.quantity);
      var newPallets = summaryData[i][9] - Number(data.pallet);
      summarySheet.getRange(i+1, 9).setValue(newQuantity);
      summarySheet.getRange(i+1, 10).setValue(newPallets);
      Logger.log("Updated row " + (i+1) + ": New quantity = " + newQuantity + ", New pallets = " + newPallets);
      updated = true;
      break;
    }
  }

  if (!updated) {
    Logger.log("No matching row found for itemCode: " + data.itemCode + ", lotNo: " + data.lotNo);
  }

  // Return the updated inventory data
  return SLgetInventoryData();
}

function formatDateForComparison(dateString) {
  if (dateString instanceof Date) {
    return Utilities.formatDate(dateString, Session.getScriptTimeZone(), "dd/MM/yyyy");
  }

  // Check if the date is in ISO format (e.g., "2024-09-02T16:00:00.000Z")
  if (dateString.includes('T')) {
    var date = new Date(dateString);
    return Utilities.formatDate(date, Session.getScriptTimeZone(), "dd/MM/yyyy");
  }

  // If it's already in dd/MM/yyyy format, return as is
  if (dateString.match(/^\d{1,2}\/\d{1,2}\/\d{4}$/)) {
    return dateString;
  }

  // If it's in the format "4/8/23  22/11/23", take the second date
  if (dateString.includes('  ')) {
    return dateString.split('  ')[1];
  }

  // If none of the above, return the original string
  return dateString;
}


