// Global spreadsheet ID
function generateTransactionReport(month, year) {
  var SPREADSHEET_ID = '17kO0MzE5mOMcQqdJgHenpSjY4Tll2KMpF8pSIdiXLUo';

  console.log('Starting generateTransactionReport for month: ' + month + ', year: ' + year);

  try {
    var ss = SpreadsheetApp.openById(SPREADSHEET_ID);
    console.log('Successfully opened spreadsheet');

    // Log all sheet names
    var allSheets = ss.getSheets();
    console.log("All sheet names:");
    allSheets.forEach(function(sheet) {
      console.log("'" + sheet.getName() + "'");
    });
  } catch (e) {
    console.error('Error opening spreadsheet: ' + e.message);
    return;
  }

  // Calculate the previous month for opening stock
  var openingStockDate = new Date(year, month - 1, 0);
  var openingStockMonth = openingStockDate.getMonth() + 1;
  var openingStockYear = openingStockDate.getFullYear();

  console.log("Input month: " + month + ", Input year: " + year);
  console.log("Calculated openingStockMonth: " + openingStockMonth);
  console.log("Calculated openingStockYear: " + openingStockYear);

  // Construct the sheet name for opening stock (previous month)
  var openingStockSheetName = getMonthAbbreviation(openingStockMonth) + " " + openingStockYear;
  console.log('Looking for opening stock sheet: "' + openingStockSheetName + '"');

  var openingStockSheet = ss.getSheetByName(openingStockSheetName);

  // Try direct access
  var directSheet = ss.getSheetByName("Jun 2024");
  console.log("Direct sheet access result: " + (directSheet ? "Found" : "Not Found"));

  if (!openingStockSheet) {
    console.error('Opening stock sheet not found: ' + openingStockSheetName);
    throw new Error("Opening stock sheet not found for " + openingStockSheetName);
  }

  console.log('Successfully found opening stock sheet');

  var stockOutSheet = ss.getSheetByName("Stock Out");
  var stockInSheet = ss.getSheetByName("Stock In");

  if (!stockOutSheet || !stockInSheet) {
    console.error('Stock Out or Stock In sheet not found');
    throw new Error("Stock Out or Stock In sheet not found");
  }

  console.log('Successfully found Stock Out and Stock In sheets');

  // Calculate the start and end dates for transactions
  var transactionStartDate = new Date(year, month - 1, 1);
  var transactionEndDate = new Date(year, month, 0);
  console.log('Transaction date range: ' + transactionStartDate + ' to ' + transactionEndDate);

  // Process opening stock sheet
  var openingStockData = openingStockSheet.getDataRange().getValues();
  console.log('Retrieved opening stock data. Rows: ' + openingStockData.length);

  var openingStock = {};
  var allItems = {};

  for (var i = 1; i < openingStockData.length; i++) {
    var key = openingStockData[i][1] + "|" + openingStockData[i][4]; // Product Description + Lot Number
    var quantity = openingStockData[i][8];
    var pallets = openingStockData[i][9];
    openingStock[key] = formatQuantity(quantity, pallets);
    allItems[key] = {productDesc: openingStockData[i][1], lotNumber: openingStockData[i][4]};
  }

  console.log('Processed opening stock data. Items: ' + Object.keys(openingStock).length);

  // Process Stock Out and Stock In sheets
  var stockOutData = stockOutSheet.getDataRange().getValues();
  var stockInData = stockInSheet.getDataRange().getValues();
  console.log('Retrieved Stock Out data. Rows: ' + stockOutData.length);
  console.log('Retrieved Stock In data. Rows: ' + stockInData.length);

  var transactions = {};

  function processSheet(data, isStockIn) {
    for (var i = 1; i < data.length; i++) {
      var date = new Date(data[i][0]);

      // Only process transactions within the selected month
      if (date >= transactionStartDate && date <= transactionEndDate) {
        var key = data[i][2] + "|" + data[i][5]; // Product Description + Lot Number
        var quantity = data[i][8];
        var pallets = data[i][9];

        if (!transactions[key]) {
          transactions[key] = [];
        }

        transactions[key].push({
          date: date,
          stockIn: isStockIn ? formatQuantity(quantity, pallets) : "",
          stockOut: !isStockIn ? formatQuantity(quantity, pallets) : ""
        });

        if (!allItems[key]) {
          allItems[key] = {productDesc: data[i][2], lotNumber: data[i][5]};
        }
      }
    }
  }

  processSheet(stockOutData, false);
  processSheet(stockInData, true);

  console.log('Processed Stock Out and Stock In data. Transaction keys: ' + Object.keys(transactions).length);

  // Generate Transaction report data
  var transactionData = [];

  for (var key in allItems) {
    var productDesc = allItems[key].productDesc;
    var lotNumber = allItems[key].lotNumber;
    var balance = openingStock[key] || "New Stock";

    // Add opening stock row
    transactionData.push([
      productDesc,
      lotNumber,
      formatDate(transactionStartDate), // First day of the selected month
      balance,
      "",
      "",
      balance
    ]);

    if (transactions[key]) {
      // Sort transactions by date
      transactions[key].sort((a, b) => a.date - b.date);

      // Add transaction rows
      for (var transaction of transactions[key]) {
        if (transaction.stockIn) {
          balance = addQuantities(balance, transaction.stockIn);
        }
        if (transaction.stockOut) {
          balance = subtractQuantities(balance, transaction.stockOut);
        }

        transactionData.push([
          "",  // Product Description (blank after first row)
          "",  // Lot Number (blank after first row)
          formatDate(transaction.date),
          "",
          transaction.stockIn,
          transaction.stockOut,
          balance
        ]);
      }
    }

    // Add an empty row between different items
    transactionData.push(["", "", "", "", "", "", ""]);
  }

  console.log('Generated transaction report. Rows: ' + transactionData.length);
  return transactionData;
}
function addQuantities(q1, q2) {
  if (q1 === "New Stock") q1 = "0 Ctns 0 Pallets";
  if (q2 === "New Stock") q2 = "0 Ctns 0 Pallets";

  var [qty1, , pallets1] = q1.split(" ");
  var [qty2, , pallets2] = q2.split(" ");
  var newQty = (parseInt(qty1) || 0) + (parseInt(qty2) || 0);
  var newPallets = (parseInt(pallets1) || 0) + (parseInt(pallets2) || 0);
  return formatQuantity(newQty, newPallets);
}

function subtractQuantities(q1, q2) {
  if (q1 === "New Stock") q1 = "0 Ctns 0 Pallets";
  if (q2 === "New Stock") q2 = "0 Ctns 0 Pallets";

  var [qty1, , pallets1] = q1.split(" ");
  var [qty2, , pallets2] = q2.split(" ");
  var newQty = (parseInt(qty1) || 0) - (parseInt(qty2) || 0);
  var newPallets = (parseInt(pallets1) || 0) - (parseInt(pallets2) || 0);
  return formatQuantity(newQty, newPallets);
}

function formatQuantity(qty, pallets) {
  qty = isNaN(qty) ? 0 : parseInt(qty);
  pallets = isNaN(pallets) ? 0 : parseInt(pallets);

  if (qty === 0 && pallets === 0) {
    return "Clear";
  }

  var qtyStr = qty + " Ctns";
  var palletStr = pallets + " " + (pallets === 1 ? "Pallet" : "Pallets");

  return qtyStr + " " + palletStr;
}

function formatDate(date) {
  var day = date.getDate().toString().padStart(2, '0');
  var month = (date.getMonth() + 1).toString().padStart(2, '0');
  var year = date.getFullYear();
  return day + "/" + month + "/" + year;
}

function getMonthAbbreviation(monthNumber) {
  var monthAbbreviations = [
    "Jan", "Feb", "Mar", "Apr", "May", "Jun",
    "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"
  ];
  return monthAbbreviations[parseInt(monthNumber) - 1];
}

// This function can be called from the client-side to generate the report
function generateReport(month, year) {
  try {
    var data = generateTransactionReport(parseInt(month), parseInt(year));
    return JSON.stringify(data);
  } catch (error) {
    return JSON.stringify({error: error.message});
  }
}


