function updateSingLongSummary(sheet, data) {
  const values = sheet.getDataRange().getValues();
  let rowIndex = -1;

  // Check if the item already exists
  for (let i = 1; i < values.length; i++) {
    if (values[i][0] === data.itemCode && 
        values[i][3] === data.expiryBatch && 
        values[i][4] === data.lotNumber) {
      rowIndex = i + 1;
      break;
    }
  }

  if (rowIndex === -1) {
    // Item doesn't exist, append new row
    sheet.appendRow([
      data.itemCode,
      data.productDescription,
      data.packingSize,
      data.expiryBatch,
      data.lotNumber,
      data.dateStored,
      data.container,
      '', // Pallet Due Date left blank
      data.quantity,
      data.pallet
    ]);
  } else {
    // Item exists, update quantity and pallet
    const currentQuantity = sheet.getRange(rowIndex, 9).getValue();
    const currentPallet = sheet.getRange(rowIndex, 10).getValue();
    sheet.getRange(rowIndex, 9).setValue(currentQuantity + Number(data.quantity));
    sheet.getRange(rowIndex, 10).setValue(currentPallet + Number(data.pallet));
  }
}

