function updateExpiryDateBatchNumber(date, itemCode, lotNumber, dateStored, container, newValue) {
  try {
    var spreadsheet = SpreadsheetApp.openById('17kO0MzE5mOMcQqdJgHenpSjY4Tll2KMpF8pSIdiXLUo');
    var stockInSheet = spreadsheet.getSheetByName('Stock In');
    var summarySheet = spreadsheet.getSheetByName('Sing Long Summary');

    if (!stockInSheet || !summarySheet) {
      return { success: false, error: 'One or both sheets not found' };
    }

    // Update Stock In sheet
    var stockInData = stockInSheet.getDataRange().getValues();
    var stockInRowIndex = findExactMatchingRow(stockInData, date, itemCode, lotNumber, dateStored, container);

    if (stockInRowIndex === -1) {
      return { success: false, error: 'Row not found in Stock In sheet' };
    }

    stockInSheet.getRange(stockInRowIndex, 5).setValue(newValue);

    // Update Sing Long Summary sheet
    var summaryData = summarySheet.getDataRange().getValues();
    var summaryRowIndex = findBestMatchingRow(summaryData, itemCode, lotNumber);

    if (summaryRowIndex === -1) {
      return { success: false, error: 'Row not found in Sing Long Summary sheet' };
    }

    summarySheet.getRange(summaryRowIndex, 4).setValue(newValue);

    return { success: true };
  } catch (error) {
    console.error('Error updating cells:', error);
    return { success: false, error: error.toString() };
  }
}

function findExactMatchingRow(data, date, itemCode, lotNumber, dateStored, container) {
  for (var i = 1; i < data.length; i++) {
    if (formatDateForComparison(data[i][0]) === formatDateForComparison(date) &&
        data[i][1] === itemCode &&
        data[i][5] === lotNumber &&
        formatDateForComparison(data[i][6]) === formatDateForComparison(dateStored) &&
        data[i][7] === container) {
      return i + 1; // +1 because sheet rows are 1-indexed
    }
  }
  return -1; // Row not found
}

function findBestMatchingRow(data, itemCode, lotNumber) {
  var matchingRows = [];
  for (var i = 1; i < data.length; i++) {
    if (data[i][0] === itemCode && data[i][4] === lotNumber) {
      matchingRows.push(i + 1); // +1 because sheet rows are 1-indexed
    }
  }

  if (matchingRows.length === 0) {
    return -1; // No matching row found
  }

  // Return the last (most recent) matching row
  return matchingRows[matchingRows.length - 1];
}

