// Function to get available dates for the request form
function getRequestFormDates(type) {
  const ss = SpreadsheetApp.openById('17kO0MzE5mOMcQqdJgHenpSjY4Tll2KMpF8pSIdiXLUo');
  const sheet = ss.getSheetByName(type === 'stockIn' ? 'Stock In' : 'Stock Out');
  const data = sheet.getRange('A2:A').getValues();

  // Get unique dates and ensure they're in DD/MM/YYYY format
  const uniqueDates = [...new Set(data.map(row => {
    if (row[0] instanceof Date) {
      return Utilities.formatDate(row[0], Session.getScriptTimeZone(), 'dd/MM/yyyy');
    }
    return row[0]; // If it's already a string, assume it's in the correct format
  }).filter(date => date !== ''))];

  // Sort dates in descending order (most recent first)
  uniqueDates.sort((a, b) => {
    const [dayA, monthA, yearA] = a.split('/').map(Number);
    const [dayB, monthB, yearB] = b.split('/').map(Number);
    return new Date(yearB, monthB - 1, dayB) - new Date(yearA, monthA - 1, dayA);
  });

  return uniqueDates;
}

