//pallet Due Date
function calculatePalletCharges(selectedMonth, selectedYear) {
  try {
    // Log the received month and year
    console.log('Received selectedMonth:', selectedMonth);
    console.log('Received selectedYear:', selectedYear);

    var spreadsheetId = '17kO0MzE5mOMcQqdJgHenpSjY4Tll2KMpF8pSIdiXLUo';
    var ss = SpreadsheetApp.openById(spreadsheetId);

    var stockOutSheet = ss.getSheetByName('Stock Out');
    var storeInSheet = ss.getSheetByName('Stock In');

    // Convert selectedMonth and selectedYear to numbers and validate
    selectedMonth = parseInt(selectedMonth);
    selectedYear = parseInt(selectedYear);

    // Log the parsed month and year
    console.log('Parsed selectedMonth:', selectedMonth);
    console.log('Parsed selectedYear:', selectedYear);

    if (isNaN(selectedMonth) || selectedMonth < 1 || selectedMonth > 12 || 
        isNaN(selectedYear) || selectedYear < 2000 || selectedYear > 2100) {
      throw new Error('Invalid month or year selected');
    }

    // Calculate previous month and year
    var previousMonth = selectedMonth - 1;
    var previousYear = selectedYear;
    if (previousMonth === 0) {
      previousMonth = 12;
      previousYear--;
    }

    // Log the calculated previous month and year
    console.log('Calculated previousMonth:', previousMonth);
    console.log('Calculated previousYear:', previousYear);

    // Get the name of the previous month sheet
    var previousMonthName = getMonthName(previousMonth) + ' ' + previousYear;
    console.log('Previous month sheet name:', previousMonthName);

    var previousMonthSheet = ss.getSheetByName(previousMonthName);

    if (!previousMonthSheet) {
      throw new Error('Previous month sheet not found: ' + previousMonthName);
    }

    var stockOutData = stockOutSheet.getRange('A2:J' + stockOutSheet.getLastRow()).getValues();
    var previousMonthData = previousMonthSheet.getRange('A2:J' + previousMonthSheet.getLastRow()).getValues();
    var storeInData = storeInSheet.getRange('A2:J' + storeInSheet.getLastRow()).getValues();

    var pallets = {};
    var storeInPallets = {};
    var startOfMonth = new Date(selectedYear, selectedMonth - 1, 1);
    var endOfMonth = new Date(selectedYear, selectedMonth, 0);

    console.log('Start of month:', startOfMonth);
    console.log('End of month:', endOfMonth);

    var thirtyDaysInMilliseconds = 30 * 24 * 60 * 60 * 1000;

    function isInTargetMonth(date) {
      return date >= startOfMonth && date <= endOfMonth;
    }

    // Process previous month data
    previousMonthData.forEach(function(row) {
      var key = row[1] + '|' + row[4]; // Product + Lot number
      var initialDueDate = new Date(row[7]);
      var palletCount = parseInt(row[9]);
      var quantity = parseInt(row[8]); // Get quantity from column I

      pallets[key] = {
        palletCount: palletCount,
        quantity: quantity,
        initialDueDate: initialDueDate,
        lastPalletDueDate: initialDueDate, // Last Pallet Due Date from column H
        nextPalletDueDate: new Date(initialDueDate.getTime() + thirtyDaysInMilliseconds), // Next Pallet Due Date is +30 days
        dailyPalletCount: {},
        dailyQuantity: {}
      };

      // Fill in daily quantities for the entire month
      var currentDate = new Date(startOfMonth);
      while (currentDate <= endOfMonth) {
        pallets[key].dailyPalletCount[currentDate.toDateString()] = palletCount;
        pallets[key].dailyQuantity[currentDate.toDateString()] = quantity;
        currentDate.setDate(currentDate.getDate() + 1);
      }
    });

    // Process Store In data
    storeInData.forEach(function(row) {
      var product = row[2]; // Product name from column C
      var lotNumber = row[5]; // Lot number from column F
      var key = product + '|' + lotNumber;
      var storedDate = new Date(row[6]); // Date Stored from column G
      var palletCount = parseInt(row[9]); // Pallet count from column J
      var quantity = parseInt(row[8]); // Get quantity from column I

      if (isInTargetMonth(storedDate)) {
        storeInPallets[key] = {
          palletCount: palletCount,
          quantity: quantity,
          initialPalletCount: palletCount,
          initialQuantity: quantity,
          storedDate: storedDate,
          lastPalletDueDate: storedDate, // Last Pallet Due Date from column G
          nextPalletDueDate: new Date(storedDate.getTime() + 29 * 24 * 60 * 60 * 1000), // Next Pallet Due Date is +29 days
          fullyWithdrawn: false
        };
      }
    });

    // Process stock out data
    stockOutData.forEach(function(row) {
      var key = row[2] + '|' + row[5]; // Product + Lot number
      var outDate = new Date(row[0]);
      var outPalletCount = parseInt(row[9]);
      var outQuantity = parseInt(row[8]); // Get quantity from column I
      if (pallets[key] && isInTargetMonth(outDate)) {
        pallets[key].palletCount = Math.max(0, pallets[key].palletCount - outPalletCount);
        pallets[key].quantity = Math.max(0, pallets[key].quantity - outQuantity);

        // Update daily quantities
        for (var d = new Date(outDate); d <= endOfMonth; d.setDate(d.getDate() + 1)) {
          if (pallets[key].dailyPalletCount[d.toDateString()] !== undefined) {
            pallets[key].dailyPalletCount[d.toDateString()] = Math.max(0, pallets[key].dailyPalletCount[d.toDateString()] - outPalletCount);
            pallets[key].dailyQuantity[d.toDateString()] = Math.max(0, pallets[key].dailyQuantity[d.toDateString()] - outQuantity);
          }
        }
      }
      if (storeInPallets[key] && isInTargetMonth(outDate)) {
        storeInPallets[key].palletCount = Math.max(0, storeInPallets[key].palletCount - outPalletCount);
        storeInPallets[key].quantity = Math.max(0, storeInPallets[key].quantity - outQuantity);
        if (storeInPallets[key].palletCount <= 0) {
          storeInPallets[key].fullyWithdrawn = true;
        }
      }
    });

    // Calculate charges
    var charges = [];
    var lotCounts = {};

    for (var key in pallets) {
      var data = pallets[key];

      var currentDueDate = new Date(data.initialDueDate);
      var chargeCount = 0;
      while (currentDueDate <= endOfMonth) {
        var nextDueDate = new Date(currentDueDate.getTime() + thirtyDaysInMilliseconds);

        if (isInTargetMonth(currentDueDate) || isInTargetMonth(nextDueDate)) {
          var chargePeriodStart = new Date(Math.max(currentDueDate, startOfMonth));
          var chargePeriodEnd = new Date(Math.min(nextDueDate, endOfMonth));

          var periodPalletCount = data.palletCount;
          var periodQuantity = data.quantity;
          for (var d = new Date(chargePeriodStart); d <= chargePeriodEnd; d.setDate(d.getDate() + 1)) {
            periodPalletCount = Math.max(periodPalletCount, data.dailyPalletCount[d.toDateString()] || 0);
            periodQuantity = Math.max(periodQuantity, data.dailyQuantity[d.toDateString()] || 0);
          }

          if (periodPalletCount > 0 || periodQuantity > 0) {
            var [product, lot] = key.split('|');
            var reason;
            if (chargeCount === 0) {
              reason = "Due Date Reached";
            } else if (chargeCount === 1 && isInTargetMonth(data.initialDueDate)) {
              reason = "Charged Twice in Same Month";
            } else {
              reason = "30 Days Period";
            }

            // Adjust Last Due Date and Next Due Date for subsequent charges
            var lastDueDate = chargeCount === 0 ? data.lastPalletDueDate : new Date(endOfMonth);
            var nextDueDate = new Date(lastDueDate.getTime() + thirtyDaysInMilliseconds);

            charges.push([product, lot, periodQuantity, periodPalletCount, reason, formatDate(lastDueDate), formatDate(nextDueDate)]);
            lotCounts[lot] = (lotCounts[lot] || 0) + 1;
            chargeCount++;
          }
        }

        currentDueDate = nextDueDate;
      }
    }

    // Add charges for all Store In pallets in the selected month, not just fully withdrawn ones
    for (var key in storeInPallets) {
      var data = storeInPallets[key];
      var [product, lot] = key.split('|');
      charges.push([product, lot, data.initialQuantity, data.initialPalletCount, "New Store in", formatDate(data.lastPalletDueDate), formatDate(data.nextPalletDueDate)]);
      lotCounts[lot] = (lotCounts[lot] || 0) + 1;
    }

    // Add a flag for repeated lot numbers
    charges = charges.map(function(charge) {
      var lot = charge[1];
      return charge.concat([lotCounts[lot] > 1]);
    });

    console.log('Charges calculated:', charges.length);
    return charges;
  } catch (error) {
    console.error('Error in calculatePalletCharges:', error);
    throw error;  // Re-throw the error to be caught by the client-side code
  }
}

function getMonthName(monthNumber) {
  var monthNames = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 
                    'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
  if (monthNumber < 1 || monthNumber > 12) {
    throw new Error('Invalid month number: ' + monthNumber);
  }
  return monthNames[monthNumber - 1];
}

