function duplicateAndFilterSheet() {
  try {
    // Use the specific spreadsheet ID
    var ss = SpreadsheetApp.openById('17kO0MzE5mOMcQqdJgHenpSjY4Tll2KMpF8pSIdiXLUo');

    // Get the "Sing Long Summary" sheet
    var sourceSheet = ss.getSheetByName("Sing Long Summary");

    // Check if the sheet exists
    if (!sourceSheet) {
      return { success: false, error: "Sheet 'Sing Long Summary' not found." };
    }

    // Get all data from the source sheet
    var data = sourceSheet.getDataRange().getValues();

    // Create an array to keep track of rows to delete
    var rowsToDelete = [];

    // Iterate through the data (skipping the header row)
    for (var i = data.length - 1; i > 0; i--) {
      if (data[i][8] === 0) {
        rowsToDelete.push(i + 1); // +1 because sheet rows are 1-indexed
      }
    }

    // Delete the rows with 0 in column I (index 8)
    rowsToDelete.forEach(function(row) {
      sourceSheet.deleteRow(row);
    });

    // Create a new date object
    var date = new Date();

    // Subtract 5 days from the current date
    date.setDate(date.getDate() - 5);

    // Format the date as MMM yyyy
    var newSheetName = Utilities.formatDate(date, Session.getScriptTimeZone(), "MMM yyyy");

    // Duplicate the filtered sheet
    var newSheet = sourceSheet.copyTo(ss);

    // Rename the new sheet
    newSheet.setName(newSheetName);

    return { success: true, newSheetName: newSheetName };
  } catch (error) {
    console.error('Error in duplicateAndFilterSheet:', error);
    return { success: false, error: error.toString() };
  }
}

const SHEET_ID = '1485ut13dOQHzyosLWs7afhWVCve6asyeGSB0RI6vv4E';

function ShipmentProcessExcelFile(formObject) {
  if (!formObject || !formObject.shipmentFileInput) {
    console.log("No file uploaded or formObject is invalid");
    return null;
  }

  var fileBlob = formObject.shipmentFileInput;
  if (!fileBlob) {
    throw new Error("No file uploaded");
  }

  var content = fileBlob.getBytes();
  var workbook = XLSX.read(content, {type: 'array'});
  var sheetName = workbook.SheetNames[0];
  var sheet = workbook.Sheets[sheetName];
  var dataArray = XLSX.utils.sheet_to_json(sheet, {header: 1});

  var data = {
    shipmentNo: getValueFromArray(dataArray, 1, 0),  // A2
    poNo: getValueFromArray(dataArray, 4, 0),        // A5
    containerNumber: getValueFromArray(dataArray, 1, 5),  // F2
    eta: getValueFromArray(dataArray, 3, 5),         // F4
    listData: getListDataUntilTotal(dataArray.slice(5))  // A6F
  };

  ShipmentSaveDataToSheet(data);
  return data;
}

function getValueFromArray(dataArray, row, col) {
  if (dataArray[row] && dataArray[row][col] !== undefined) {
    return dataArray[row][col].toString();
  }
  return "";
}

function getListDataUntilTotal(data) {
  var result = [];
  for (var i = 0; i < data.length; i++) {
    // 6(A-F)
    var row = data[i].slice(0, 6);
    row = ensureRowLength(row, 6);
    row = formatColumnE(row);

    if (isTotalInColumnC(row)) {
      console.log(`Stopped at row ${i + 6} ('total' detected in column C)`);
      break;
    }
    result.push(row);
  }
  console.log(`Retrieved ${result.length} rows of data`);
  return result;
}


function ensureRowLength(row, minLength) {
  while (row.length < minLength) {
    row.push("");  // Add empty strings for missing columns
  }
  return row;
}

function formatColumnE(row) {
  // Format column E (index 4) as text with leading zeros
  if (row.length > 4 && row[4] !== "") {
    var value = String(row[4]).replace(/^'/, '').padStart(8, '0');
    row[4] = "'" + value;  // Add a single quote to force text format
  }
  return row;
}

function isTotalInColumnC(row) {
  // Check if the cell in column C (index 2) contains 'total'
  var cellValue = row[2];
  if (cellValue === undefined || cellValue === null || cellValue === "") {
    return false;
  }
  return String(cellValue).toLowerCase().includes('total');
}

function ShipmentSaveDataToSheet(data) {
  console.log("Data received:", JSON.stringify(data));
  console.log("List data length:", data.listData.length);
  console.log("List data first row:", JSON.stringify(data.listData[0]));
  var ss = SpreadsheetApp.openById(SHEET_ID);
  var shipmentListSheet = ss.getSheetByName("Shipment List");

  // Append row with available data
  var newRow = [
    data.shipmentNo || "",
    data.poNo || "",
    data.containerNumber || "",
    data.eta || ""
  ];

  try {
    shipmentListSheet.appendRow(newRow);
  } catch (error) {
    console.error("Error appending row to Shipment List:", error.message);
  }

  // Create or get sheet for this shipment's table data
  var tableSheet = ss.getSheetByName(data.shipmentNo) || ss.insertSheet(data.shipmentNo);
  tableSheet.clear();

  // Write table data
  if (data.listData && data.listData.length > 0) {
    try {
      var numColumns = data.listData[0].length;
      tableSheet.getRange(1, 1, data.listData.length, numColumns).setValues(data.listData);
    } catch (error) {
      console.error("Error writing table data:", error.message);
    }
  }

  console.log("ShipmentSaveDataToSheet completed");
}

function ShipmentGetStoredData() {
  var ss = SpreadsheetApp.openById(SHEET_ID);
