  var shipments = shipmentListSheet.getRange(2, 1, Math.max(lastRow - 1, 1), 7).getValues(); // Changed to 7 columns

  // Ensure each row has 7 columns and format the date
  shipments = shipments.map(row => {
    while (row.length < 7) { // Changed to 7 columns
      row.push("");
    }
    // Format ETA date
    if (row[3] instanceof Date) {
      row[3] = Utilities.formatDate(row[3], Session.getScriptTimeZone(), "dd/MM/yyyy");
    } else if (typeof row[3] === 'string') {
      try {
        var parsedDate = parseDate(row[3]);
        row[3] = Utilities.formatDate(parsedDate, Session.getScriptTimeZone(), "dd/MM/yyyy");
      } catch (e) {
        console.log("Failed to parse date: " + row[3]);
      }
    }
    return row;
  });

  shipmentListSheet.getRange(2, 4, lastRow - 1, 1).setNumberFormat("DD/MM/YYYY");

  return shipments;
}

function parseDate(dateString) {
  const [day, month, year] = dateString.split('/');
  return new Date(year, month - 1, day);
}

