function getTempRecordData() {
  var ss = SpreadsheetApp.openById('15jj7ZQgL1fIPsHr-kl3s2Quy7TrLsjQ-sjV4-lcus8o');
  var sheet = ss.getSheetByName('Coldroom Temp Rcd');
  var data = sheet.getDataRange().getValues();

  // Remove header row
  data.shift();

  // Group data by date and coldroom
  var groupedTempData = groupTempDataByDateAndColdroom(data);

  // Generate HTML table
  var html = generateTempRecordTable(groupedTempData);

  return html;
}

function groupTempDataByDateAndColdroom(data) {
  var groupedData = {};
  data.forEach(function(row) {
    var coldroom = row[0];
    var date = Utilities.formatDate(new Date(row[1]), Session.getScriptTimeZone(), 'dd/MM/yyyy');
    var time = Utilities.formatDate(new Date(row[2]), Session.getScriptTimeZone(), 'HH:mm');
    var temp = row[3];
    var checkBy = row[4];

    // Parse the time and add 39 minutes
    var timeDate = new Date('1970-01-01T' + time + ':00');
    timeDate.setMinutes(timeDate.getMinutes() + 39);
    var newTime = Utilities.formatDate(timeDate, Session.getScriptTimeZone(), 'HH:mm');

    if (!groupedData[date]) {
      groupedData[date] = {};
    }
    if (!groupedData[date][coldroom]) {
      groupedData[date][coldroom] = [];
    }
    groupedData[date][coldroom].push({time: newTime, temp: temp, checkBy: checkBy});
  });

  // Sort entries by time for each date and coldroom
  Object.keys(groupedData).forEach(function(date) {
    Object.keys(groupedData[date]).forEach(function(coldroom) {
      groupedData[date][coldroom].sort(function(a, b) {
        return a.time.localeCompare(b.time);
      });
    });
  });

  // Create a new object with sorted dates in descending order
  var sortedGroupedData = {};
  Object.keys(groupedData).sort(function(a, b) {
    return new Date(b.split('/').reverse().join('-')) - new Date(a.split('/').reverse().join('-'));
  }).forEach(function(date) {
    sortedGroupedData[date] = groupedData[date];
  });

  return sortedGroupedData;
}

function generateTempRecordTable(groupedTempData) {
  var html = '<div class="table-container"><table border="1">';
  var coldrooms = [
    'Blk 15', 'Blk 15 Chiller', 'Coldroom 6', 'Coldroom 6 Chiller', 'Coldroom 5c Chiller',
    'Coldroom 5 - 1', 'Coldroom 5 - 2', 'Coldroom 3B Chiller', 'Coldroom 3A Chiller',
    'Coldroom 3 Chiller', 'Coldroom 2', 'Coldroom 1'
  ];

  html += '<thead><tr><th class="sticky-col">Date</th>';
  coldrooms.forEach(function(coldroom, index) {
    html += '<th colspan="3" class="coldroom-header">' + coldroom + '</th>';
    if (index < coldrooms.length - 1) {
      html += '<th class="padding-column"></th>';
    }
  });
  html += '</tr>';

  html += '<tr><th class="sticky-col"></th>';
  coldrooms.forEach(function(_, index) {
    html += '<th>Time</th><th>Temp</th><th>Check By</th>';
    if (index < coldrooms.length - 1) {
      html += '<th class="padding-column"></th>';
    }
  });
  html += '</tr></thead>';

  html += '<tbody>';
  // This is the amended part for correct date sorting
  var dates = Object.keys(groupedTempData).sort(function(a, b) {
    var dateA = a.split('/').reverse().join('-');
    var dateB = b.split('/').reverse().join('-');
    return new Date(dateB) - new Date(dateA);
  });

  dates.forEach(function(date, dateIndex) {
    var maxEntries = 1;
    coldrooms.forEach(function(coldroom) {
      if (groupedTempData[date][coldroom]) {
        maxEntries = Math.max(maxEntries, groupedTempData[date][coldroom].length);
      }
    });

    for (var i = 0; i < maxEntries; i++) {
      html += '<tr' + (i === maxEntries - 1 ? ' class="last-row-in-date"' : '') + '>';
      if (i === 0) {
        html += '<td class="sticky-col" rowspan="' + maxEntries + '">' + date + '</td>';
      }
      coldrooms.forEach(function(coldroom, index) {
        if (groupedTempData[date][coldroom] && groupedTempData[date][coldroom][i]) {
          var entry = groupedTempData[date][coldroom][i];
          html += '<td>' + entry.time + '</td><td>' + entry.temp + '</td><td>' + entry.checkBy + '</td>';
        } else {
          html += '<td></td><td></td><td></td>';
        }
        if (index < coldrooms.length - 1) {
          html += '<td class="padding-column"></td>';
        }
      });
      html += '</tr>';
    }

    // Add padding row between dates, except after the last date
    if (dateIndex < dates.length - 1) {
      html += '<tr class="date-padding-row"><td colspan="' + (coldrooms.length * 4) + '"></td></tr>';
    }
  });

  html += '</tbody></table></div>';
  return html;
}
